/* syntax highlight*/

figure.highlight,
.codeblock {
    background: #f7f8f8;
    margin: 10px 0;
    line-height: 1.1em;
    color: #333;
    padding-top: 15px;
    overflow: hidden;
    table {
        display: block;
        width: 100%;
    }
    // Reset for tag `pre` and  for class `.gutter`, `.code`, `.tag`
    pre,
    .gutter,
    .code,
    .tag {
        background-color: inherit;
        font-family: Menlo, Consolas, monospace;
        border: none;
        padding: 0;
        margin: 0;
        // To override cursor attribute of `.tag` components
        cursor: text;
    }
    // To align line number and line of code regardless if there is a scroll bar or not
    .gutter,
    .code {
        vertical-align: top;
    }
    // hide gutter when code is plain text
    &.plain {
        .gutter {
            display: none;
        }
    }
    // Meta bar which contains name of the file and his link
    figcaption {
        font-size: 13px;
        padding: 0 15px 20px;
        margin: 0;
        background: #f7f8f8;
        color: #999999;
        a {
            float: right;
            color: #01579f;
        }
    }
    // Gutter which contains line numbers
    .gutter {
        background: #f7f8f8;
        border-right: 1px solid #e6e6e6;
        padding: 0.3em 15px;
        user-select: none;
    }
    // Code container
    .code {
        padding: 0.3em 15px 0.3em 1em;
        width: 100%;
        pre {
            max-width: 700px;
            overflow-x: auto;
            overflow-y: hidden;
        }
    }
    // All lines in gutter and code container
    .line {
        height: 1.3em;
        font-size: 13px;
    }
}

// Gist
.gist {
    .line,
    .line-number {
        font-family: Menlo, Consolas, monospace;
        font-size: 1em;
        margin: 0 0 5px 0;
        user-select: none;
    }
}

// Highlight code coloration
.highlight {
    // General
    .comment {
        color: #969896;
    }
    .string {
        color: #183691;
    }
    .keyword {
        color: #a71d5d;
    }
    // ApacheConf
    &.apacheconf .code {
        .common,
        .nomarkup,
        .attribute,
        .variable,
        .cbracket,
        .keyword {
            color: #0086b3;
        }
        .sqbracket {
            color: #df5000;
        }
        .section,
        .tag {
            color: #63a35c;
        }
    }
    // Bash
    &.bash .code {
        .shebang {
            color: #969896;
        }
        .literal,
        .built_in {
            color: #0086b3;
        }
        .variable {
            color: #333;
        }
        .title {
            color: #795da3;
        }
    }
    // coffescript
    &.coffeescript .code {
        .title {
            color: #795da3;
        }
        .literal,
        .built_in,
        .number {
            color: #0086b3;
        }
        .reserved,
        .attribute {
            color: #1d3e81;
        }
        .subst,
        .regexp,
        .attribute {
            color: #df5000;
        }
    }
    // C/C++
    &.cpp .code,
    &.c .code {
        .preprocessor {
            color: #df5000;
        }
        .meta-keyword {
            color: #a71d5d;
        }
        .title {
            color: #795da3;
        }
        .number,
        .built_in {
            color: #0086b3;
        }
    }
    // Clojure
    &.clj .code {
        .builtin-name {
            color: #df5000;
        }
        .name {
            color: #795da3;
        }
        .number {
            color: #0086b3;
        }
    }
    // C#
    &.cs .code {
        .preprocessor,
        .preprocessor .keyword {
            color: #333;
        }
        .title {
            color: #795da3;
        }
        .number,
        .built_in {
            color: #0086b3;
        }
        .xmlDocTag,
        .doctag {
            color: #63a35c;
        }
    }
    // CSS
    &.css .code {
        .at_rule,
