/* post navigator*/

.post-nav {
    overflow: hidden;
    margin-top: 15px;
    margin-bottom: 20px;
    padding: 10px;
    white-space: nowrap;
    border-top: 1px solid #eee;
    a {
        display: inline-block;
        line-height: 25px;
        font-size: 15px;
        color: #555;
        border-bottom: none;
        float: left;
        &.pre {
            float: left;
            &:before {
                font-family: "FontAwesome";
                content: "\f0d9";
                padding-right: 0.3em;
            }
        }
        &.next {
            float: right;
            &:after {
                font-family: "FontAwesome";
                content: "\f0da";
                padding-left: 0.3em;
            }
        }
        &:hover {
            border-bottom: none;
            color: #222;
        }
    }
}


/* toc*/

.toc-article {
    border: 1px solid #bbb;
    border-radius: 7px;
    margin: 1.1em 0 0 2em;
    padding: 0.7em 0.7em 0 0.7em;
    max-width: 40%;
}

.toc-title {
    font-size: 120%;
}

#toc {
    line-height: 1em;
    float: right;
    .toc {
        padding: 0;
        margin: 0.5em;
        line-height: 1.8em;
        li {
            list-style-type: none;
        }
    }
    .toc-child {
        margin-left: 1em;
        padding-left: 0;
    }
}

@media print,
screen and (max-width: 48em) {
    .toc-article {
        margin: 0em;
        max-width: 100%;
    }
    .clear {
        padding: 2.1em 0em 0em 0em;
    }
    #toc {
        float: none;
    }
}


/* table*/

table {
    margin: auto auto 15px;
    width: 100%;
    background: transparent;
    border-collapse: collapse;
    border-spacing: 0;
    text-align: left;
    th {
        font-weight: bold;
        padding: 5px 10px;
        border-bottom: 2px solid #909ba2;
    }
    td {
        padding: 5px 10px;
    }
    tr {
        &:nth-child(2n) {
            background: #f7f8f8;
        }
    }
}


/* article-share*/

.article-share-link {
    cursor: pointer;
    float: right;
    margin-left: 20px;
    &:before {
        font-family: "FontAwesome";
        content: "\f064";
        padding-right: 6px;
    }
}

.article-share-box {
    position: absolute;
    display: none;
    background: #fff;
    box-shadow: 1px 2px 10px rgba(0, 0, 0, 0.2);
    border-radius: 3px;
    margin-left: -145px;
    overflow: hidden;
    z-index: 1;
    &.on {
        display: block;
    }
}

.article-share-input {
    width: 100%;
    background: none;
    box-sizing: border-box;
    font: 14px "Helvetica Neue", Helvetica, Arial, sans-serif;
    padding: 0 15px;
    color: #555;
    outline: none;
    border: 1px solid #ddd;
    border-radius: 3px 3px 0 0;
    height: 36px;
    line-height: 36px;
}

.article-share-links {
    clearfix: none;
    background: #eee;
}

.article-share-element {
    width: 50px;
    height: 36px;
    display: block;
    float: left;
    position: relative;
    color: #999;
    text-shadow: 0 1px #fff;
    &:before {
        font-size: 20px;
        font-family: "FontAwesome";
        width: 20px;
        height: 20px;
        position: absolute;
        top: 50%;
        left: 50%;
        margin-top: -10px;
        margin-left: -10px;
        text-align: center;
    }
    &:hover {
        color: #fff;
    }
}

.article-share-twitter {
    @extend .article-share-element;
    &:before {
        content: "\f099";
    }
    &:hover {
        background: #00aced;
    }
}

.article-share-facebook {
    @extend .article-share-element;
    &:before {
        content: "\f09a";
    }
    &:hover {
        background: #3b5998;
    }
}

.article-share-weibo {
    @extend .article-share-element;
    &:before {
        content: "\f18a";
    }
    &:hover {
        background: #d44137;
    }
}

.article-share-qrcode {
    @extend .article-share-element;
    &:before {
        content: "\f029";
    }
    &:hover,
    &:active {
        background: #38ad5a;
        &~.qrcode {
            display: block;
            text-align: center;
        }
    }
}

.qrcode {
    display: none;
}


/* search result*/

ul.search-result-list {
    padding-left: 10px;
}

a.search-result-title {
    font-weight: bold;
    font-size: 15px;
    color: #555;
}

p.search-result {
    color: #444;
    text-align: justify;
}

em.search-keyword {
    font-weight: bold;
    font-style: normal;
    color: #01579f;
}


/* Disqus Button */

.disqus_click_btn {
    line-height: 30px;
    margin: 0;
    min-width: 50px;
    padding: 0 14px;
    display: inline-block;
    font-family: "Roboto", "Helvetica", "Arial", sans-serif;
    font-size: 14px;
    font-weight: 400;
    letter-spacing: 0;
    overflow: hidden;
    will-change: box-shadow;
    transition: box-shadow .2s cubic-bezier(.4, 0, 1, 1), background-color .2s cubic-bezier(.4, 0, .2, 1), color .2s cubic-bezier(.4, 0, .2, 1);
    outline: 0;
    cursor: pointer;
    text-decoration: none;
    text-align: center;
    vertical-align: middle;
    border: 0;
    background: rgba(158, 158, 158, .2);
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    color: #fff;
    background-color: #999999;
    text-shadow: 0
}

