/* post navigator*/
.post-nav {
  overflow: hidden;
  margin-top: 15px;
  margin-bottom: 20px;
  padding: 10px;
  white-space: nowrap;
  border-top: 1px solid #eee; }
  .post-nav a {
    display: inline-block;
    line-height: 25px;
    font-size: 15px;
    color: #555;
    border-bottom: none;
    float: left; }
    .post-nav a.pre {
      float: left; }
      .post-nav a.pre:before {
        font-family: "FontAwesome";
        content: "\f0d9";
        padding-right: 0.3em; }
    .post-nav a.next {
      float: right; }
      .post-nav a.next:after {
        font-family: "FontAwesome";
        content: "\f0da";
        padding-left: 0.3em; }
    .post-nav a:hover {
      border-bottom: none;
      color: #222; }

/* toc*/
.toc-article {
  border: 1px solid #bbb;
  border-radius: 7px;
  margin: 1.1em 0 0 2em;
  padding: 0.7em 0.7em 0 0.7em;
  max-width: 40%; }

.toc-title {
  font-size: 120%; }

#toc {
  line-height: 1em;
  float: right; }
  #toc .toc {
    padding: 0;
    margin: 0.5em;
    line-height: 1.8em; }
    #toc .toc li {
      list-style-type: none; }
  #toc .toc-child {
    margin-left: 1em;
    padding-left: 0; }

@media print, screen and (max-width: 48em) {
  .toc-article {
    margin: 0em;
    max-width: 100%; }
  .clear {
    padding: 2.1em 0em 0em 0em; }
  #toc {
    float: none; } }

/* table*/
table {
  margin: auto auto 15px;
  width: 100%;
  background: transparent;
  border-collapse: collapse;
  border-spacing: 0;
  text-align: left; }
  table th {
    font-weight: bold;
    padding: 5px 10px;
    border-bottom: 2px solid #909ba2; }
  table td {
    padding: 5px 10px; }
  table tr:nth-child(2n) {
    background: #f7f8f8; }

/* article-share*/
.article-share-link {
  cursor: pointer;
  float: right;
  margin-left: 20px; }
  .article-share-link:before {
    font-family: "FontAwesome";
    content: "\f064";
    padding-right: 6px; }

.article-share-box {
  position: absolute;
  display: none;
  background: #fff;
  box-shadow: 1px 2px 10px rgba(0, 0, 0, 0.2);
  border-radius: 3px;
  margin-left: -145px;
  overflow: hidden;
  z-index: 1; }
  .article-share-box.on {
    display: block; }

.article-share-input {
  width: 100%;
  background: none;
  box-sizing: border-box;
  font: 14px "Helvetica Neue", Helvetica, Arial, sans-serif;
  padding: 0 15px;
  color: #555;
  outline: none;
  border: 1px solid #ddd;
  border-radius: 3px 3px 0 0;
  height: 36px;
  line-height: 36px; }

.article-share-links {
  clearfix: none;
  background: #eee; }

.article-share-element, .article-share-twitter, .article-share-facebook, .article-share-weibo, .article-share-qrcode {
  width: 50px;
  height: 36px;
  display: block;
  float: left;
  position: relative;
  color: #999;
  text-shadow: 0 1px #fff; }
  .article-share-element:before, .article-share-twitter:before, .article-share-facebook:before, .article-share-weibo:before, .article-share-qrcode:before {
    font-size: 20px;
    font-family: "FontAwesome";
    width: 20px;
    height: 20px;
    position: absolute;
    top: 50%;
    left: 50%;
    margin-top: -10px;
    margin-left: -10px;
    text-align: center; }
  .article-share-element:hover, .article-share-twitter:hover, .article-share-facebook:hover, .article-share-weibo:hover, .article-share-qrcode:hover {
    color: #fff; }

.article-share-twitter:before {
  content: "\f099"; }

.article-share-twitter:hover {
  background: #00aced; }

.article-share-facebook:before {
  content: "\f09a"; }

.article-share-facebook:hover {
  background: #3b5998; }

.article-share-weibo:before {
  content: "\f18a"; }

.article-share-weibo:hover {
  background: #d44137; }

.article-share-qrcode:before {
  content: "\f029"; }

.article-share-qrcode:hover, .article-share-qrcode:active {
  background: #38ad5a; }
  .article-share-qrcode:hover ~ .qrcode, .article-share-qrcode:active ~ .qrcode {
    display: block;
    text-align: center; }

.qrcode {
  display: none; }

/* search result*/
ul.search-result-list {
  padding-left: 10px; }

a.search-result-title {
  font-weight: bold;
  font-size: 15px;
  color: #555; }

p.search-result {
  color: #444;
  text-align: justify; }

em.search-keyword {
  font-weight: bold;
  font-style: normal;
  color: #01579f; }

/* Disqus Button */
.disqus_click_btn {
  line-height: 30px;
  margin: 0;
  min-width: 50px;
  padding: 0 14px;
  display: inline-block;
  font-family: "Roboto", "Helvetica", "Arial", sans-serif;
  font-size: 14px;
  font-weight: 400;
  letter-spacing: 0;
  overflow: hidden;
  will-change: box-shadow;
  transition: box-shadow 0.2s cubic-bezier(0.4, 0, 1, 1), background-color 0.2s cubic-bezier(0.4, 0, 0.2, 1), color 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  outline: 0;
  cursor: pointer;
  text-decoration: none;
  text-align: center;
  vertical-align: middle;
  border: 0;
  background: rgba(158, 158, 158, 0.2);
  box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.2), 0 1px 5px 0 rgba(0, 0, 0, 0.12);
  color: #fff;
  background-color: #999999;
  text-shadow: 0; }

